import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
import './noticiacompleta.css'
import Header from '../../components/Header/index'
import NavHeader from '../../components/NavHeader/NavHeader';

const NoticiaIndividual = () => {

  const [posts, setPosts] = useState([]);
  const [imageUrls, setImageUrls] = useState([]);
  const [post, setPost] = useState(null);
  const [imageUrl, setImageUrl] = useState("");

  const { id } = useParams();
  console.log(id);

  useEffect(() => {
    axios
      .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/noticias/${id}`)
      .then((response) => {
        const { data } = response;
        setPost(data);

        axios
          .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/noticias`)
          .then((response) => {
            const { data } = response;
            setPosts(data);
          });

        axios
          .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/upload/files`)
          .then((response) => {
            const { data } = response;
            const filteredData = data.filter((file) => file.caption === "teste");
            if (filteredData.length > 0) {
              const url = "https://hammerhead-app-5cwy4.ondigitalocean.app" + filteredData[0].url;
              setImageUrl(url);
            }
          });
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  useEffect(() => {
    const urls = posts.map((post) => {
      const filteredData = post?.relationships?.field_noticia_image?.data ? imageUrls.filter((file) => file.fid === post.relationships.field_noticia_image.data.id) : [];
      return filteredData.length > 0 ? "https://hammerhead-app-5cwy4.ondigitalocean.app" + filteredData[0]?.attributes?.url : "";
    });
    setImageUrls(urls);
  }, [posts]);

  return (
    <>
      <NavHeader />
      <Header />
      <section>
        <div className="container-noticias__individual">
          <div className="main-news">
            {post && (
              <div className="container_noticias__individual">
                <div className="portofolio__item-img__individual">
                  <img className="news-img" src={imageUrl} alt={post?.attributes?.titulo} />
                </div>
                <h2>{post?.attributes?.titulo}</h2>
                <p>{post?.attributes?.texto}</p>
                <div className="portofolio__item-cta__individual">
                  <Link to={`/noticias`} className="btn">
                    Voltar
                  </Link>
                </div>
              </div>
            )}
          </div>
        </div>
      </section>
    </>
  );
};

export default NoticiaIndividual;
