import React, { useState } from 'react';
import axios from 'axios';

const Album = () => {
  const [selectedYear, setSelectedYear] = useState('');
  const [photos, setPhotos] = useState([]);

  const anos = ['2016', '2017', '2018', '2023']; // Exemplo de anos disponíveis

  const handleYearSelect = async (year) => {
    setSelectedYear(year);
    try {
      const response = await axios.get(`http://localhost:1337/api/albums?populate=*${year}`); // Rota da API para obter os álbuns do ano selecionado
      const photosData = response.data.data.map((album) => album.attributes.fotos.data); // Obtém a lista de fotos de cada álbum
      const photosList = photosData.flat().map((photo) => ({ // Mapeia e extrai as informações necessárias de cada foto
        id: photo.id,
        url: photo.attributes.url,
        title: photo.attributes.name,
      }));
      setPhotos(photosList); // Atualiza o estado com as fotos recebidas
    } catch (error) {
      console.log('Erro ao buscar as fotos:', error);
    }
  };

  return (
    <div className="album">
      <div className="year-dropdown">
        <span>Selecione um ano:</span>
        <select value={selectedYear} onChange={(e) => handleYearSelect(e.target.value)}>
          <option value="">Selecione...</option>
          {anos.map((ano) => (
            <option key={ano} value={ano}>
              {ano}
            </option>
          ))}
        </select>
      </div>

      {selectedYear && (
        <div className="photos">
          {photos.length === 0 ? (
            <div className="no-photos">Nenhuma foto disponível para o ano selecionado.</div>
          ) : (
            photos.map((photo) => (
              <div key={photo.id} className="photo">
                <img src={photo.url} alt={photo.title} />
                <div className="caption">{photo.title}</div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default Album;
