import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import './noticiacompleta.css'
import Header from '../../components/Header/index'
import NavHeader from '../../components/NavHeader/NavHeader';

const NoticiaCompleta = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get(`https://shark-app-6myi8.ondigitalocean.app/api/noticias/${id}?populate=*`)
      .then((response) => {
        const { data } = response.data;
        setPost(data);
      })
      .catch((error) => {
        console.log(error);
      });

    axios.get("https://shark-app-6myi8.ondigitalocean.app/api/noticias?populate=*")
      .then((response) => {
        const { data } = response.data;
        const sortedData = data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        const filteredData = sortedData.slice(0, 9);
        setPosts(filteredData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  if (!post) {
    return <p>Carregando...</p>;
  }

  return (
    <>
      <NavHeader />
      <Header />
      <div className="englobador">
        <div className="container_noticias_variadas">
          <div>
            <h1>Notícias</h1>
            <hr size="6" width="100%" align="left" color="black"></hr>
          </div>
          {posts.map((post, index) => (
            <div key={post.id} className="container_news">
              <div className="container_news_img">
                <img src={post?.attributes.imagem.data.attributes.url} alt={post?.attributes?.titulo} loading="lazy" />
              </div>
              <div className="container_news_texto">
                <h3 className="container_news_titulo">{post?.attributes?.titulo}</h3>
                <p className="container_news_texto">{post?.attributes?.texto}</p>
                <div className="news-link-container">
                  <Link to={`/noticias/${post?.id}`} className="news-link">
                    Ver notícia completa
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="container_noticia_individual">
          <div className="container-noticiascompleta_img">
            <img src={post?.attributes.imagem.data.attributes.url} alt={post?.attributes?.titulo} loading="lazy" />
          </div>
          <h2>{post?.attributes?.titulo}</h2>
          <p>{post?.attributes?.texto}</p>
          <div className="portofolio__item-cta-completa">
            <Link to={`/noticias`} className="btn">Voltar</Link>
          </div>
        </div>
      </div>
    </>
  );
};

export default NoticiaCompleta;
