import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavHeader from '../../components/NavHeader/NavHeader';
import Header from '../../components/Header';
import { PainelNoticias } from '../../components/Noticias/PainelNoticias';
import Footer from '../../components/footer/Footer';

export const Equipes = () => {
  const [posts, setPosts] = useState([]);
  const [images, setImages] = useState({});
  const [imageUrl, setImageUrl] = useState(null); // adicionado

  useEffect(() => {
    axios.get("https://hammerhead-app-5cwy4.ondigitalocean.app/api/calendarios")
      .then((response) => {
        const { data } = response.data;
        setPosts(data);
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/upload/files`)
      .then((response) => {
        const { data } = response;
        const filteredData = data.filter((file) => file.caption === "calendario");
        if (filteredData.length > 0) {
          const url = "https://hammerhead-app-5cwy4.ondigitalocean.app" + filteredData[0].url;
          setImageUrl(url);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []); // removido o array de dependências

  return (
    <>
      <NavHeader />
      <Header />
      {posts.map((post, index) => {
        const eventImages = images[post.id] || [];
        return (
          <div className='container_noticias' key={post.id}>
            <h2>{post?.attributes?.local}</h2>
            <p>{post?.attributes?.data}</p>
            {post && (
              <div className="container_noticias__individual">
                <div className="portofolio__item-img__individual">
                  <img src={imageUrl} alt={post?.attributes?.titulo} />
                </div>
                <h2>{post?.attributes?.titulo}</h2>
                <p>{post?.attributes?.texto}</p>
                <div className="portofolio__item-cta__individual">
                
                </div>
              </div>
            )}
          </div>
        );
      })}

      <Footer />
    </>
  );
};
