import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Album = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/albums?populate=*")
      .then((response) => {
        const { data } = response.data;
        const sortedData = data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        const filteredData = sortedData.slice(0, 9);
        setPosts(filteredData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

 return (
  <div className="news-container">
    <div>
      <h1>Noticias</h1>
      <hr size="6" width="100%" align="left" color="black"></hr>
    </div>
    <div className="news-grid">
      {posts.map((post, index) => (
        <div key={post.id} className="news-item">
          <div className="news-img-container">
            <img
              src={post?.attributes.imagem.fotos?.data.attributes.formats.small.url}
            />
          </div>
        </div> // Adicione a tag de fechamento aqui
      ))}
    </div>
  </div>
);
};

export default Album;
