import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavHeader from '../../components/NavHeader/NavHeader';
import Header from '../../components/Header'
import { PainelNoticias } from '../../components/Noticias/PainelNoticias'
import Footer from '../../components/footer/Footer';

export const Equipes = () => {
  // estado para armazenar os eventos do calendário
  const [calendarios, setCalendarios] = useState([]);

  useEffect(() => {
    // busca os dados da API de calendário com Axios
    axios.get('https://hammerhead-app-5cwy4.ondigitalocean.app/api/calendarios')
      .then(response => {
        // verifica se os dados são um array antes de atualizar o estado
        if (Array.isArray(response.data)) {
          setCalendarios(response.data);
        }
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  return (
    <>
      <NavHeader />
      <Header />
      <section>
        {/* verifica se há eventos do calendário antes de mapeá-los */}
        {calendarios.length > 0 ? calendarios.map(calendario => (
          <div className="calendario-card" key={calendario.id}>
            <p>Data: {calendario.Data}</p>
            <p>Local: {calendario.Local}</p>
          </div>
        )) : <p>Nenhum evento encontrado.</p>}
      </section>
      
      <Footer />
    </>
  );
};
