import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './dropdownequipes.css'

const Album = () => {
  const [posts, setPosts] = useState({});

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/albums?populate=*")
      .then((response) => {
        const { data } = response.data;
        console.log("Data:", data);
        
        const sortedData = data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        const filteredData = sortedData.slice(0, 9);
        console.log("Filtered Data:", filteredData);
        
        // Separar as fotos por ano
        const photosByYear = {};
        filteredData.forEach((post) => {
          const year = post.attributes.Ano;
          if (!photosByYear[year]) {
            photosByYear[year] = [];
          }
          photosByYear[year].push(post.attributes.fotos.data[0]);
        });
        
        setPosts(photosByYear);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className="news-container">
      <div>
        <h1>Noticias</h1>
        <hr size="6" width="100%" align="left" color="black" />
      </div>
      <div className="news-grid">
        {Object.entries(posts).map(([year, photos]) => (
          <div key={year}>
            <h2>{year}</h2>
            {photos.map((photo, index) => (
              <div key={photo.id} className="news-item">
                <div className="news-img-container">
                  <img
                    src={photo.attributes.url}
                    alt="Imagem"
                  />
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Album;
