import axios from 'axios';
import fileType from 'file-type';

axios.get('https://hammerhead-app-5cwy4.ondigitalocean.app/api/upload/files')
  .then((response) => {
    const { data } = response;

    const filteredData = data.filter((file) => file.caption === 'calendario');

    // cria um objeto com as URLs das imagens para cada identificador
    const imagesObj = {};
    filteredData.forEach((file) => {
      const id = file.tags[0];
      const fileTypeBuffer = Buffer.from(file.data);
      const { mime } = fileType(fileTypeBuffer) || {};
      if (mime && mime.startsWith('image/')) {
        if (!imagesObj[id]) {
          imagesObj[id] = [];
        }
        imagesObj[id].push(`https://hammerhead-app-5cwy4.ondigitalocean.app${file.url}`);
      }
    });
    setImages(imagesObj);
  })
  .catch((error) => {
    console.log(error);
  });
