import React, { useState, useEffect } from "react";
import axios from "axios";

const Equipes = () => {
  const [associatedId1, setAssociatedId1] = useState("");
  const [associatedId2, setAssociatedId2] = useState("");
  const [image1Url, setImage1Url] = useState("");
  const [image2Url, setImage2Url] = useState("");
  const [images, setImages] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get(
        "https://api.npoint.io/22a92589f056c35f849d"
      );
      setAssociatedId1(result.data.associatedId1);
      setAssociatedId2(result.data.associatedId2);
    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchImages = async () => {
      const image1 = await axios.get(
        `https://api.npoint.io/${associatedId1}`
      );
      const image2 = await axios.get(
        `https://api.npoint.io/${associatedId2}`
      );
      setImages({
        [associatedId1]: image1.data.url,
        [associatedId2]: image2.data.url,
      });
    };
    if (associatedId1 && associatedId2) {
      fetchImages();
    }
  }, [associatedId1, associatedId2]);

  return (
    <div>
      {Object.keys(images).length === 2 ? (
        <div>
          <img src={images[associatedId1]} alt="Imagem 1" />
          <img src={images[associatedId2]} alt="Imagem 2" />
        </div>
      ) : (
        <p>Carregando imagens...</p>
      )}
    </div>
  );
};

export default Equipes;
