import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavHeader from '../../components/NavHeader/NavHeader';
import Header from '../../components/Header'
import { PainelNoticias } from '../../components/Noticias/PainelNoticias'
import Footer from '../../components/footer/Footer';

export const Equipes = () => {
  // estado para armazenar os eventos do calendário
  const [posts, setPosts] = useState([]);
  const [images, setImages] = useState({});

  useEffect(() => {
    axios.get("https://hammerhead-app-5cwy4.ondigitalocean.app/api/calendarios")
      .then((response) => {
        const { data } = response.data;
        setPosts(data);
      })
      .catch((error) => {
        console.log(error);
      });

    axios
      .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/upload/files`)
      .then((response) => {
        const { data } = response;
        const filteredData = data.filter((file) => file.caption === "calendario");

        // cria um objeto com as URLs das imagens para cada identificador
        const imagesObj = {};
        filteredData.forEach((file) => {
          const id = file.tags[0];
          if (!imagesObj[id]) {
            imagesObj[id] = [];
          }
          imagesObj[id].push("https://hammerhead-app-5cwy4.ondigitalocean.app" + file.url);
        });
        setImages(imagesObj);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []); // coloque os valores que você quer monitorar dentro desse array de dependências

  return (
    <>
      <NavHeader />
      <Header />
      {posts.map((post, index) => {
        console.log("identificador", post);
        const eventImages = images[post.id] || []; // obtém as imagens correspondentes a este evento
        return (
          <div className='container_noticias' key={post.id}>
            <h2>{post?.attributes?.local}</h2>
            <p>{post?.attributes?.data}</p>
            <div className="portofolio__item-img__individual">
              {eventImages.map((url, i) => (
                <img src={url} alt={`${post?.attributes?.local} - Imagem ${i + 1}`} key={i} />
              ))}
            </div>
          </div>
        );
      })}

      <Footer />
    </>
  );
};
