import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
import './noticiacompleta.css'
import Header from '../../components/Header/index'
import NavHeader from '../../components/NavHeader/NavHeader';

const NoticiaIndividual = () => {

  const [posts, setPosts] = useState([]);
  const [imageUrls, setImageUrls] = useState([]);
  const [post, setPost] = useState(null);
  const [imageUrl, setImageUrl] = useState("");

  const { id } = useParams();
  console.log(id);

  useEffect(() => {
    axios
      .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/noticias/${id}`)
      .then((response) => {
        const { data } = response.data;
        setPost(data);

        axios
          .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/noticias`)
          .then((response) => {
            const { data } = response.data;
            setPosts(data);
          });

        axios
          .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/upload/files`)
          .then((response) => {
            const { data } = response;
            const filteredData = data.filter((file) => file.caption === "teste");
            if (filteredData.length > 0) {
              const url = "https://hammerhead-app-5cwy4.ondigitalocean.app" + filteredData[0].url;
              setImageUrl(url);
            }
          });
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  return (
    <>
      <NavHeader />
      <Header />
      <section>
        <div className="container-noticias__individual">
          <div className="main-news">
            {posts.map((post, index) => (
              <div key={post.id} className="news-item">
                <div className="news-img-container">
                  <img className="news-img" src={imageUrls[index]} alt={post?.attributes?.titulo} />
                </div>
                <div className="news-text-container">
                  <h3 className="news-text-title">{post?.attributes?.titulo}</h3>
                  <p className="news-text-content">{post?.attributes?.texto}</p>
                  <div className="news-link-container">
                    <Link to={`/noticias/${post?.id}`} className="news-link">Ver notícia completa</Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </>
  );
};

export default NoticiaIndividual;
