import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavHeader from '../../components/NavHeader/NavHeader';
import Header from '../../components/Header'
import { PainelNoticias } from '../../components/Noticias/PainelNoticias'
import Footer from '../../components/footer/Footer';

export const Equipes = () => {
  // estado para armazenar os eventos do calendário
  const [posts, setPosts] = useState([]);
  const [imageUrl, setImageUrl] = useState(null);

  useEffect(() => {
    axios.get("https://hammerhead-app-5cwy4.ondigitalocean.app/api/calendarios")
      .then((response) => {
        const { data } = response.data;
        setPosts(data);
      })
      .catch((error) => {
        console.log(error);
      }); 

    axios
      .get(`https://hammerhead-app-5cwy4.ondigitalocean.app/api/upload/files`)
      .then((response) => {
        const { data } = response;
        const filteredData = data.filter((file) => file.caption === "calendario");
        if (filteredData.length > 0) {
          const url = "https://hammerhead-app-5cwy4.ondigitalocean.app" + filteredData[0].url;
          setImageUrl(url);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []); // coloque os valores que você quer monitorar dentro desse array de dependências

  return (
    <>
      <NavHeader />
      <Header />
      {posts.map((post, index) => {
        console.log("identificador", post);
        return (
          <div className='container_noticias'>
            <h2>{post?.attributes?.local}</h2>
            <p>{post?.attributes?.data}</p>
            <div className='portofolio__item-cta'>
              {/* Adicione qualquer outro conteúdo que precise aqui */}
            </div>
          </div>
        );
      })}
      <Footer />
    </>
  );
};
