import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Album = () => {
  const [posts, setPosts] = useState({});

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/albums?populate=*")
      .then((response) => {
        const { data } = response.data;
        console.log("Data:", data);

        // Separar as fotos por ano
        const photosByYear = {};
        data.forEach((post) => {
          const year = post.attributes.Ano;
          if (!photosByYear[year]) {
            photosByYear[year] = [];
          }
          photosByYear[year].push(...post.attributes.fotos.data);
        });

        setPosts(photosByYear);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className="album-container">
      <div>
        <h1>√Ålbum</h1>
        <hr size="6" width="100%" align="left" color="black" />
      </div>
      <div className="album-grid">
        {Object.entries(posts).map(([year, photos]) => (
          <div key={year} className="album-year">
            <h2>{year}</h2>
            <div className="album-photos">
              {photos.map((photo, index) => (
                <div key={photo.id} className="album-photo">
                  <img
                    src={photo.attributes.url}
                    alt="Imagem"
                  />
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Album;
