import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Album = () => {
  const [posts, setPosts] = useState({});
  const [selectedYear, setSelectedYear] = useState('');

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/albums?populate=*")
      .then((response) => {
        const { data } = response.data;
        console.log("Data:", data);

        // Separar as fotos por ano
        const photosByYear = {};
        data.forEach((post) => {
          const year = post.attributes.Ano;
          if (!photosByYear[year]) {
            photosByYear[year] = [];
          }
          photosByYear[year].push(...post.attributes.fotos.data);
        });

        setPosts(photosByYear);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleYearClick = (year) => {
    setSelectedYear(year);
  };

  return (
    <div className="album-container">
      <div>
        <h1>√Ålbum</h1>
        <hr size="6" width="100%" align="left" color="black" />
      </div>
      <div className="album-dropdown">
        <select
          value={selectedYear}
          onChange={(e) => handleYearClick(e.target.value)}
        >
          <option value="">Selecione um ano</option>
          {Object.keys(posts).map((year) => (
            <option key={year} value={year}>
              {year}
            </option>
          ))}
        </select>
      </div>
      <div className="album-grid">
        {selectedYear && (
          <div className="album-year">
            <h2>{selectedYear}</h2>
            <div className="album-photos">
              {posts[selectedYear].map((photo, index) => (
                <div key={photo.id} className="album-photo">
                  <img src={photo.attributes.url} alt="Imagem" />
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Album;
